mapper.select:
select
    v.id,v.create_time,v.update_time,v.is_deleted,v.ve_id,v.video_url,v.video_long,v.video_status,
    ve.ve_name as veName
from
    video v,ve_info ve

    ve.id = v.ve_id and v.is_deleted = 0

       and ve.ve_addressid = #{addressid}


       and v.video_status = #{veStatus}



           ve.ve_typeid = #{typeid}



order by
